angular.module("truncate",[]).filter("characters",function(){return function(e,t,n){if(isNaN(t))return e;if(t<=0)return"";if(e&&e.length>t){e=e.substring(0,t);if(!n){var r=e.lastIndexOf(" ");if(r!==-1){e=e.substr(0,r)}}else{while(e.charAt(e.length-1)===" "){e=e.substr(0,e.length-1)}}return e+"..."}return e}}).filter("words",function(){return function(e,t){if(isNaN(t))return e;if(t<=0)return"";if(e){var n=e.split(/\s+/);if(n.length>t){e=n.slice(0,t).join(" ")+"..."}}return e}})